import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [form, setForm] = useState({});
  const [users, setUsers] = useState([]);

  const handleForm = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:8080/api/adduser", {
      method: "POST",
      body: JSON.stringify(form),
      headers: {
        "Content-Type": "application/json",
      },
    });

    const data = await response.json();
    setUsers((prev) => [...prev, data]);
    console.log(data);
  };

  const getUsers = async () => {
    const response = await fetch("http://localhost:8080/api/getuser", {
      method: "GET",
    });
    const data = await response.json();
    setUsers(data);
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <div>
    <div className="left-pg">
      <form onSubmit={handleSubmit}>
        <label for="name">Name</label>
        <input type="text" name="name" onChange={handleForm}></input>
        <label for="email">E-mail</label>
        <input type="email" name="email" onChange={handleForm}></input>Â {" "}
        <label for="contact">contact no:</label>
        <input type="number" name="contact" onChange={handleForm}></input>
        <button type="submit">submit</button>
      </form>
      </div>
      <div className="right-pg">
        <ul>
          {users.map((user) => (
            <li key={user._id}>
              {user.username},{user.password}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
